query:
PREFIX omecore: <https://ld.openmicroscopy.org/core/>
PREFIX omens: <http://www.openmicroscopy.org/ns/default/>
SELECT distinct ?investigation ?study ?assay ?img_name WHERE {
    ?img a omecore:Image;
         omens:Investigation ?investigation ;
         omens:Study ?study;
         omens:Assay ?assay .
}
